mapper.select:
select
*
from users where id = #{id} and registerMethod = #{registerMethod}

select
*
from users where id = #{id} and registerMethod =
#{registerMethod} and password = #{password}

mapper.update:
update users set realname
= #{realname}
, nickname = #{nickname}
, email1 = #{email1}
, email2 = #{email2}
, password = #{password}
, gender = #{gender}
, phone1 = #{phone1}
, phone2 = #{phone2}
, phone3 = #{phone3}
, postcode = #{postcode}
, addr1 = #{addr1}
, addr2 = #{addr2}
, contentProfile = #{contentProfile}
, IMGPROFILE = #{imgProfile}
, account = #{account}
, dateRegist=sysdate where
id=#{id} and registerMethod =#{registerMethod}
